mapper.insert:

    		SELECT CONCAT('PR', LPAD(NEXTVAL(SEQ_AD_STD_PRICE_0), 5, '0'))

		INSERT INTO AD_STD_PRICE(
			  PRICE_TYPE
			, AD_LVL
			, AD_POS
			, PRICE_ID
			, UNIT_AMT
			, INST_AMT
			, REMARK
		)
		VALUES(
			#{priceType}
		  , #{adLvl}
		  , #{adPos}
		  , #{priceId}
		  , #{unitAmt}
		  , #{instAmt}
		  , #{remark}
		)

mapper.update:
UPDATE AD_STD_PRICE
SET
	  PRICE_TYPE = #{priceType}
	, AD_LVL = #{adLvl}
	, AD_POS = #{adPos}
	, UNIT_AMT = #{unitAmt}
	, INST_AMT = #{instAmt}
	, REMARK = #{remark}
WHERE
	PRICE_ID = #{priceId}
