mapper.insert:

	SELECT CONCAT('IN', LPAD(NEXTVAL(SEQ_AD_INST_INFO_0), 5, '0'))


INSERT INTO AD_INST_INFO(
	  INST_ID
	, INST_NM
	, CUST_ID
	, AD_ST_DATE
	, AD_ED_DATE
	, AD_AMT
	, REMARK
	, CONFIRM_YN
	, PRICE_TYPE
)
VALUES(
	  #{instId}
	, #{instNm}
	, #{custId}
	, #{adStDate}
	, #{adEdDate}
	, #{adAmt}
	, #{remark}
	, 'N'
	, #{priceType}
)

mapper.update:
UPDATE AD_INST_INFO
SET
	  INST_NM = #{instNm}
	, CUST_ID = #{custId}
	, AD_ST_DATE = #{adStDate}
	, AD_ED_DATE = #{adEdDate}
	, AD_AMT = #{adAmt}
	, REMARK = #{remark}
WHERE
	INST_ID = #{instId}

mapper.insert:
INSERT INTO AD_INST_DL(
	  INST_ID
	, VHC_ID
	, AD_POS
	, PRICE_TYPE
)
VALUES

(
	  #{instId}
	, #{item.vhcId}
	, #{item.adPos}
	, #{priceType}
)

mapper.delete:
 DELETE ID FROM AD_INST_DL ID
	 LEFT OUTER JOIN AD_INST_INFO I
	 ON ID.INST_ID = I.INST_ID
	 AND I.PRICE_TYPE = #{priceType}
 WHERE
 	ID.INST_ID = #{instId}

