mapper.insert:

 		SELECT CONCAT('CT', LPAD(NEXTVAL(SEQ_BM_CUST_INFO_0), 5, '0'))


 	INSERT INTO BM_CUST_INFO(
 		  CUST_ID
 		, CUST_NM
 		, CORP_NO
 		, EMAIL
 		, PHONE
 		, FAX
 		, ADDR1
 		, ZIP_NO
 		, ADDR2
 		, REMARK
 		, CREATED_AT
 		, CREATED_BY
 		, UPDATED_AT
 		, UPDATED_BY)
		VALUES(
			  #{custId}
			, #{custNm}
			, #{corpNo}
			, #{email}
			, #{phone}
			, #{fax}
			, #{addr1}
			, #{zipNo}
			, #{addr2}
			, #{remark}
			, #{createdAt}
			, #{createdBy}
			, #{updatedAt}
			, #{updatedBy}
		)

mapper.update:
UPDATE BM_CUST_INFO
SET
	  CUST_NM		=	#{custNm}
	, CORP_NO		=	#{corpNo}
	, EMAIL			=	#{email}
	, PHONE			=	#{phone}
	, FAX			=	#{fax}
	, ADDR1			=	#{addr1}
	, ZIP_NO		=	#{zipNo}
	, ADDR2			=	#{addr2}
	, REMARK		=	#{remark}
	, UPDATED_AT	=	#{updatedAt}
	, UPDATED_BY	=	#{updatedBy}
WHERE
	CUST_ID = #{custId}
