 		, REMARK
 		, USE_YN    		
 		, CREATED_AT
 		, CREATED_BY
 		, UPDATED_AT
 		, UPDATED_BY
 		)
		VALUES(
			  #{vhcId}
			, #{vhcNo}
			, #{chasNo}
			, #{corpId}
			, #{area}
			, #{maker}
			, #{relsDate}
			, #{modelNm}
			, #{vhcKind}
