			, #{remark}
			, #{useYn}
			, #{createdAt}
			, #{createdBy}
			, #{updatedAt}
			, #{updatedBy}
			)

mapper.update:
UPDATE BM_VHC_INFO
SET
	  VHC_NO		=	#{vhcNo}
	, CHAS_NO		=	#{chasNo}
	, CORP_ID		=	#{corpId}
	, AREA	 		=	#{area}
	, MAKER			=	#{maker}
	, RELS_DATE		=	#{relsDate}
	, MODEL_NM		=	#{modelNm}
	, VHC_KIND		=	#{vhcKind}
	, VHC_TYPE		=	#{vhcType}
	, LF_YN			=	#{lfYn}
	, VHC_FUEL		=	#{vhcFuel}
	, REMARK		= 	#{remark}
	, USE_YN		=	#{useYn}
	, UPDATED_AT	=	#{updatedAt}
	, UPDATED_BY	=	#{updatedBy}
WHERE
	VHC_ID = #{vhcId}
