mapper.insert:
    	INSERT INTO
			BM_ROUT_INFO(
						  ROUT_ID
						, ROUT_NM
						, ST_STA_NM
		                , ED_STA_NM
		                , CREATED_AT
		                , CREATED_BY
		                , UPDATED_AT
		                , UPDATED_BY
		                )

			VALUES 

					( #{voList.routId}
					, #{voList.routNm}
			        , #{voList.stStaNm}
			        , #{voList.edStaNm}
			        , #{voList.createdAt}
			        , #{voList.createdBy}
			        , #{voList.updatedAt}
			        , #{voList.updatedBy}
			        )

			ON DUPLICATE KEY UPDATE

				  ROUT_NM		= VALUES(ROUT_NM)
				, ST_STA_NM		= VALUES(ST_STA_NM)
				, ED_STA_NM		= VALUES(ED_STA_NM)
				, UPDATED_AT	= VALUES(UPDATED_AT)
				, UPDATED_BY	= VALUES(UPDATED_BY)

mapper.update:
UPDATE
	BM_ROUT_INFO
SET
	  WAY_DIV		= #{wayDiv}
    , TURN_DIV		= #{turnDiv}
    , UPDATED_AT	= #{updatedAt}
    , UPDATED_BY	= #{updatedBy}
WHERE
	ROUT_ID = #{routId}
