mapper.insert:
    	INSERT INTO
			BM_ROUT_INFO(
						  ROUT_ID
						, INTER_ROUT_ID
						, ROUT_NM
						, SHORT_ROUT_NM
						, DVC_NAME
						, WAY_INFO
						, DIR_INFO						
						, ST_STA_NM
		                , ED_STA_NM
		                , CREATED_AT
		                , CREATED_BY
		                , UPDATED_AT
		                , UPDATED_BY
		                )

			VALUES 

					( #{voList.routId}
					, #{voList.routId}
					, #{voList.routNm}
					, #{voList.routNm}
					, #{voList.routNm}
					, SUBSTR(#{voList.stStaNm}, 1, 10)
			        , SUBSTR(#{voList.edStaNm}, 1, 10) 
			        , #{voList.stStaNm}
			        , #{voList.edStaNm}
			        , #{voList.createdAt}
			        , #{voList.createdBy}
			        , #{voList.updatedAt}
			        , #{voList.updatedBy}
			        )

			ON DUPLICATE KEY UPDATE

				  ROUT_NM		= VALUES(ROUT_NM)
				, ST_STA_NM		= VALUES(ST_STA_NM)
				, ED_STA_NM		= VALUES(ED_STA_NM)
				, UPDATED_AT	= VALUES(UPDATED_AT)
				, UPDATED_BY	= VALUES(UPDATED_BY)

mapper.update:
UPDATE
	BM_ROUT_INFO
SET
	  WAY_DIV		= #{wayDiv}
	, USER_WAY_DIV 	= #{wayDiv}
    , TURN_DIV		= #{turnDiv}
    , UPDATED_AT	= #{updatedAt}
    , UPDATED_BY	= #{updatedBy}
WHERE
	INTER_ROUT_ID = #{interRoutId}

UPDATE
	BM_ROUT_INFO
SET
	  WAY_DIV		= #{wayDiv}
    , TURN_DIV		= #{turnDiv}
    , UPDATED_AT	= #{updatedAt}
    , UPDATED_BY	= #{updatedBy}
WHERE
	INTER_ROUT_ID = #{interRoutId}

update.foreach:
UPDATE
	BM_ROUT_INFO
SET
	  SHORT_ROUT_NM	= #{voList.shortRoutNm}
	, WAY_INFO		= #{voList.wayInfo}
    , USER_WAY_DIV	= #{voList.userWayDiv}
    , DVC_NAME		= #{voList.dvcName}
    , LINE1_STR		= #{voList.line1Str}
    , LINE2_STR		= #{voList.line2Str}
    , LINE1_SATSTR	= #{voList.line1Satstr}
    , LINE2_SATSTR	= #{voList.line2Satstr}
    , LINE1_SUNSTR	= #{voList.line1Sunstr}
    , LINE2_SUNSTR	= #{voList.line2Sunstr}
    , UPDATED_AT	= #{voList.updatedAt}
    , UPDATED_BY	= #{voList.updatedBy}
WHERE
	ROUT_ID = #{voList.routId}

mapper.insert:

    		SELECT CONCAT('ROUT', LPAD(NEXTVAL(SEQ_BM_ROUT_INFO_0), 5, '0'))

		INSERT INTO
			BM_ROUT_INFO(
				  ROUT_ID
		        , INTER_ROUT_ID
		        , ROUT_NM
		        , SHORT_ROUT_NM
		        , WAY_INFO
		        , DIR_INFO
		        , WAY_DIV
		        , USER_WAY_DIV
		        , ST_STA_NM
		        , ED_STA_NM
		        , DVC_NAME
		        , LINE1_STR
		        , LINE2_STR
		        , LINE1_SATSTR
		        , LINE2_SATSTR
		        , LINE1_SUNSTR
		        , LINE2_SUNSTR
		        )
			VALUES(
				    #{routId}
				  , #{interRoutId}
				  , #{routNm}
				  , #{shortRoutNm}
				  , #{wayInfo}
				  , #{dirInfo}
  				  , (SELECT TXT_VAL1 FROM SM_DL_CD_INFO WHERE CO_CD = 'UPDOWN_FLAG' AND DL_CD = #{wayDiv})
				  , (SELECT TXT_VAL1 FROM SM_DL_CD_INFO WHERE CO_CD = 'UPDOWN_FLAG' AND DL_CD = #{userWayDiv})
				  , #{stStaNm}
				  , #{edStaNm}
				  , #{dvcName}
				  , #{line1Str}
				  , #{line2Str}
				  , #{line1Satstr}
				  , #{line2Satstr}
				  , #{line1Sunstr}
				  , #{line2Sunstr}
				  )

mapper.update:
UPDATE BM_ROUT_INFO
SET
	  INTER_ROUT_ID	= #{interRoutId}
    , ROUT_NM 		= #{routNm}
    , SHORT_ROUT_NM	= #{shortRoutNm}
    , WAY_INFO		= #{wayInfo}
   	, DIR_INFO		= #{dirInfo}
   	, WAY_DIV		= (SELECT TXT_VAL1 FROM SM_DL_CD_INFO WHERE CO_CD = 'UPDOWN_FLAG' AND DL_CD = #{wayDiv})
    , USER_WAY_DIV	= (SELECT TXT_VAL1 FROM SM_DL_CD_INFO WHERE CO_CD = 'UPDOWN_FLAG' AND DL_CD = #{userWayDiv})
    , ST_STA_NM		= #{stStaNm}
    , ED_STA_NM		= #{edStaNm}
    , DVC_NAME		= #{dvcName}
    , LINE1_STR		= #{line1Str}
    , LINE2_STR		= #{line2Str}
    , LINE1_SATSTR	= #{line1Satstr}
    , LINE2_SATSTR	= #{line2Satstr}
    , LINE1_SUNSTR	= #{line1Sunstr}
    , LINE2_SUNSTR	= #{line2Sunstr}
 WHERE
 	ROUT_ID = #{routId}
