mapper.select:
SELECT ROUT_ID , ROUT.SEQ , STA.STA_ID , STA_NM , STA_NO , LATI , LONGI , DATE_FORMAT(STA.UPDATED_AT, '%Y-%m-%d %T') AS UPDATED_AT FROM BM_ROUT_STA_INFO ROUT LEFT JOIN BM_STA_INFO STA ON ROUT.STA_ID = STA.STA_ID WHERE ROUT.ROUT_ID = #{routId}
AND( STA.STA_ID LIKE CONCAT('%', #{filter}, '%') OR STA_NO LIKE CONCAT('%', #{filter}, '%') OR STA_NM LIKE CONCAT('%', #{filter}, '%') )
ORDER BY ROUT.SEQ

mapper.insert:


    	INSERT INTO
			BM_ROUT_STA_INFO(
							  ROUT_ID
							, SEQ
							, STA_ID
			)			
		VALUES

				( #{voList.routId}
				, #{voList.seq}
		        , #{voList.staId}
		        )

    	INSERT INTO
			BM_STA_INFO ( STA_ID
						, STA_NM
		                , STA_NO
		                , LATI
		                , LONGI
		                , CREATED_AT
		                , CREATED_BY
		                , UPDATED_AT
		                , UPDATED_BY
		                )
				VALUES

						( #{voList.staId}
						, REPLACE(#{voList.staNm}, ',', '.')
						, #{voList.staNo}
						, #{voList.lati}
						, #{voList.longi}
						, #{voList.createdAt}
						, #{voList.createdBy}
						, #{voList.updatedAt}
						, #{voList.updatedBy}
						)

		ON DUPLICATE KEY UPDATE
			  STA_NM		= VALUES(STA_NM)
		    , STA_NO		= VALUES(STA_NO)
		    , LATI			= VALUES(LATI)
		    , LONGI			= VALUES(LONGI)
		    , UPDATED_AT	= VALUES(UPDATED_AT)
		    , UPDATED_BY	= VALUES(UPDATED_BY)
