mapper.select:
SELECT ROUT_ID , SEQ , NODE_TYPE , NODE_ID , NODE_NM , LATI , LONGI , UPDATED_AT , UPDATED_BY FROM BM_ROUT_NODE_INFO WHERE ROUT_ID = #{routId}
AND( NODE_ID LIKE CONCAT('%', #{filter1}, '%') OR NODE_NM LIKE CONCAT('%', #{filter1}, '%') )
ORDER BY SEQ

mapper.insert:

    	INSERT INTO
    		BM_ROUT_NODE_INFO
    			(
    				  NODE_ID
    				, NODE_NM
    				, LATI
    				, LONGI
    				, SEQ
    				, ROUT_ID
    				, STA_ID
    				, NODE_TYPE
    				, CREATED_AT
    				, CREATED_BY
    				, UPDATED_AT
    				, UPDATED_BY
    			)
		VALUES

			(


						#{voList.nodeId}


						#{voList.staId}


	  			    	CONCAT('ND', LPAD(NEXTVAL(SEQ_BM_ROUT_NODE_INFO_0), 8, '0'))


				, #{voList.nodeNm}
				, #{voList.lati}
				, #{voList.longi} 
				, #{voList.seq}
				, #{voList.routId}
				, #{voList.staId}
				, #{voList.nodeType}
				, #{voList.createdAt}
				, #{voList.createdBy}
				, #{voList.updatedAt}
				, #{voList.updatedBy}
			)

    	INSERT INTO
    		BM_ROUT_RESULT
    			(
    				  NODE_ID
    				, NODE_NM
    				, NODE_TYPE
    				, LATI
    				, LONGI
    				, SEQ
    				, ROUT_ID
    			)
		VALUES

			(
			  	  #{voList.nodeId}
			  	, #{voList.nodeNm}
				, #{voList.nodeType}
				, #{voList.lati}
				, #{voList.longi} 
				, #{voList.seq}
				, #{voList.routId}
			)

mapper.delete:
 		DELETE

		FROM
			BM_ROUT_NODE_INFO
		WHERE
			ROUT_ID = #{routId}
		    AND
		    NODE_ID = #{nodeId}
		;

		DELETE
		FROM
			BM_ROUT_RESULT
		WHERE
			ROUT_ID = #{routId}
			AND
			NODE_ID = #{nodeId}
		;
