mapper.insert:
    	INSERT INTO
    		BM_ROUT_NODE_INFO
    			(
    				  NODE_ID
    				, NODE_NM
    				, LATI
    				, LONGI
    				, SEQ
    				, ROUT_ID
    				, STA_ID
    				, CREATED_AT
    				, CREATED_BY
    				, UPDATED_AT
    				, UPDATED_BY
    			)
		VALUES

			(
  			      CONCAT('VND', LPAD(NEXTVAL(SEQ_BM_ROUT_NODE_INFO_0), 6, '0'))
				, #{voList.nodeNm}
				, #{voList.lati}
				, #{voList.longi} 
				, #{voList.seq}
				, #{voList.routId}
				, #{voList.staId}
				, #{voList.createdAt}
				, #{voList.createdBy}
				, #{voList.updatedAt}
				, #{voList.updatedBy}
			)
