mapper.insert:

 		SELECT CONCAT('DV', LPAD(NEXTVAL(SEQ_BM_DVC_INFO_0), 5, '0'))

 	INSERT INTO BM_DVC_INFO(
 		  DVC_ID
 		, MAKER
 		, DVC_KIND
 		, INST_LOC
 		, MNG_ID
 		, DVC_IP
 		, REMARK
 		, VHC_ID
 		, USE_YN 		
 		, CREATED_AT
 		, CREATED_BY
 		, UPDATED_AT
 		, UPDATED_BY
 		)
		VALUES(
			  #{dvcId}
			, #{maker}
			, #{dvcKind}
			, #{instLoc}
			, #{mngId}
			, #{dvcIp}
			, #{remark}
			, #{vhcId}
			, "N"
			, #{createdAt}
			, #{createdBy}
			, #{updatedAt}
			, #{updatedBy}
			)


 		SELECT NEXTVAL(SEQ_BM_DVC_HIST_0)

 	INSERT INTO BM_DVC_HIST(
 		  DEV_SERIAL_NO
 		, MODEL_NM
 		, APLY_DATE
 		, WORK_TYPE
 		, WORK_AMT
 		, REMARK
 		, DVC_ID
 		, SEQ
 		, CREATED_AT
 		, CREATED_BY
 		, UPDATED_AT
 		, UPDATED_BY
 		)
		VALUES(
			  #{devSerialNo}
			, #{modelNm}
			, #{aplyDate}
			, #{workType}
			, #{workAmt}
			, #{remark}
			, #{dvcId}
			, #{seq}
			, #{createdAt}
			, #{createdBy}
			, #{updatedAt}
			, #{updatedBy}
			)

mapper.update:
		UPDATE BM_DVC_INFO
    	SET
    		  USE_YN        =   "Y"
    		, UPDATED_AT	=	#{updatedAt}
    		, UPDATED_BY	=	#{updatedBy}
    	WHERE
    		DVC_ID = #{dvcId}

		UPDATE BM_DVC_INFO
    	SET
    	      MNG_ID        =   NULL
    		, USE_YN        =   "N"
    		, UPDATED_AT	=	#{updatedAt}
    		, UPDATED_BY	=	#{updatedBy}
    	WHERE
    		DVC_ID = #{dvcId}

UPDATE BM_DVC_INFO
SET
	  DVC_ID		=	#{dvcId}
	, MAKER			=	#{maker}
	, DVC_KIND		=	#{dvcKind}
	, INST_LOC		=	#{instLoc}
	, MNG_ID		=	#{mngId}
	, DVC_IP		=	#{dvcIp}
	, REMARK		=	#{remark}
	, VHC_ID		=	#{vhcId}
	, UPDATED_AT	=	#{updatedAt}
	, UPDATED_BY	=	#{updatedBy}
WHERE
	DVC_ID = #{dvcId}
