mapper.update:
		UPDATE BM_DVC_HIST
		SET
			 DEV_SERIAL_NO = #{devSerialNo}
    	   , MODEL_NM      = #{modelNm}
    	   , APLY_DATE     = #{aplyDate}
    	   , WORK_TYPE     = #{workType}
    	   , WORK_AMT      = #{workAmt}
   		   , REMARK        = #{remark}
   		   , DVC_ID        = #{dvcId}
   		   , UPDATED_AT	   = #{updatedAt}
    	   , UPDATED_BY	   = #{updatedBy}
    	WHERE
    		DVC_ID = #{dvcId}
    		AND DEV_SERIAL_NO = #{devSerialNo}
