mapper.update:
	UPDATE SM_DL_CD_INFO
	SET
		TXT_VAL1 = #{txtVal1}
	  , NUM_VAL4 = #{numVal4}
	  , NUM_VAL5 = #{numVal5}
	  , NUM_VAL6 = #{numVal6}
  	WHERE
  	  DL_CD = "CD043"

		UPDATE BM_NEWS_SET
		SET		
			PROV_ID     =   #{provId}
		  , PROV_URL    =   #{provUrl}
		  , PROV_NM     =   #{provNm}
		  , REMARK      =   #{remark}
		  , USE_YN      =   #{useYn}
		  , UPDATED_AT	=	#{updatedAt}
    	  , UPDATED_BY	=	#{updatedBy}
    	  , UPDATED_IP	=	#{updatedIp}
		WHERE
			PROV_ID = #{provId}
