mapper.update:
		UPDATE BM_NEWS
		SET		
			CATEGORY    =   #{category}
		  , PROV_NM     =   #{provNm}
		  , NEWS_TITLE  =   #{newsTitle}
		  , PUB_DT      =   #{pubDt}
		  , USE_YN      =   #{useYn}
		  , RENEW_DATE  =   #{renewDate}
		  , UPDATED_AT	=	#{updatedAt}
    	  , UPDATED_BY	=	#{updatedBy}
    	  , UPDATED_IP	=	#{updatedIp}
		WHERE
			SEQ = #{seq}

		UPDATE BM_NEWS_USER_SET
		SET		
			USER_NEWS_ID     =   #{userNewsId}
		  , REMARK     	  	 =   #{remark}
		  , USE_YN      	 =   #{useYn}
		  , CATEGORY    	 =   #{category}
		  , PROV_NM     	 =   #{provNm}
		  , PUB_DT      	 =   #{pubDt}
		  , UPDATED_AT		 =	 #{updatedAt}
    	  , UPDATED_BY		 =	 #{updatedBy}
    	  , UPDATED_IP		 =	 #{updatedIp}
		WHERE
			USER_NEWS_ID     =   #{userNewsId}
