mapper.insert:

    		SELECT CONCAT('VI', LPAD(NEXTVAL(SEQ_BM_VDO_INFO_0), 5, '0'))


		INSERT INTO
			BM_VDO_INFO(  VDO_ID
						, CON_ID
						, VDO_NM
		                , PLAY_ST_DATE
		                , PLAY_ED_DATE
		                , FILE_TYPE
		                , ATT_FILE
		                , PLAY_TM
		                , IMG_PLAY_TM
		                , REMARK
		                , CREATED_AT
		                , CREATED_BY
		                , UPDATED_AT
		                , UPDATED_BY)

			VALUES	(	  #{vdoId}
						, #{conId}
						, #{vdoNm}
						, #{playStDate}
						, #{playEdDate}
						, #{fileType}
						, #{attFile}
						, #{playTm}
						, #{imgPlayTm}
						, #{remark}
						, #{createdAt}
						, #{createdBy}
						, #{updatedAt}
						, #{updatedBy})

mapper.update:
		UPDATE BM_VDO_INFO
    	SET
    		  VDO_NM		=	#{vdoNm}
    		, CON_ID		=	#{conId}
    		, PLAY_ST_DATE	=	#{playStDate}
    		, FILE_TYPE		=	#{fileType}
    		, IMG_PLAY_TM	=	#{imgPlayTm}
    		, ATT_FILE		=	#{attFile}
    		, REMARK		=	#{remark}
    		, PLAY_TM		=	#{playTm}
    		, PLAY_ED_DATE	=	#{playEdDate}
    		, UPDATED_AT	=	#{updatedAt}
    		, UPDATED_BY	=	#{updatedBy}
    	WHERE
    		VDO_ID = #{vdoId}
