mapper.select:
SELECT * FROM (SELECT DATE_FORMAT(A.PLAY_DATE, '%Y-%m-%d %H:%i:%s') AS PLAY_DATE , H.VHC_ID , A.ID , IFNULL(V.CON_ID, VD.CON_ID) AS CON_ID , IFNULL(DC1.DL_CD_NM, DC2.DL_CD_NM) AS AD_TYPE , IFNULL(V.VOC_NM, VD.VDO_NM) AS AD_NM FROM BM_AD_LOG A LEFT OUTER JOIN BM_DVC_INFO D ON A.MNG_ID = D.MNG_ID LEFT OUTER JOIN BM_VHC_INFO H ON D.VHC_ID = H.VHC_ID LEFT OUTER JOIN BM_VOC_INFO V ON A.ID = V.VOC_ID LEFT OUTER JOIN BM_VDO_INFO VD ON A.ID = VD.VDO_ID LEFT OUTER JOIN SM_DL_CD_INFO DC1 ON LEFT(A.ID, 2) = DC1.TXT_VAL1 AND DC1.CO_CD = 'VOC_DIV' LEFT OUTER JOIN SM_DL_CD_INFO DC2 ON LEFT(A.ID, 2) = DC2.TXT_VAL1 AND DC2.CO_CD = 'VDO_DIV') R WHERE CON_ID IS NOT NULL AND VHC_ID = #{vhcId} AND CON_ID = #{conId}
AND PLAY_DATE BETWEEN DATE_FORMAT(#{stDate}, '%Y-%m-%d 00:00:00') AND DATE_FORMAT(#{edDate}, '%Y-%m-%d 23:59:59')
ORDER BY PLAY_DATE DESC

mapper.insert:
INSERT INTO BM_AD_LOG(
	  PLAY_DATE
	, MNG_ID
	, ID
	, AD_TYPE
	, AD_NM
)
VALUES

(
	  #{item.playDate}
	, #{item.mngId}
	, #{item.id}
	, #{adType}
	, #{adNm}
)
