mapper.select:
SELECT NODE_ID , NODE_NM , NODE_TYPE , LATI , LONGI , SEQ , ROUT_ID FROM BM_ROUT_SIMULATION WHERE ROUT_ID = #{routId}
AND( NODE_ID LIKE CONCAT('%', #{filter1}, '%') OR NODE_NM LIKE CONCAT('%', #{filter1}, '%') )
ORDER BY SEQ

