mapper.insert:
 	INSERT INTO SM_DL_CD_INFO(
 		  CO_CD
 		, DL_CD
 		, DL_CD_NM
 		, DL_CD_ENM
 		, SORT_ODR
 		, TXT_VAL1
 		, TXT_VAL2
 		, TXT_VAL3
 		, NUM_VAL4
 		, NUM_VAL5
 		, NUM_VAL6
 		, USE_YN
 		, REMARK
 		, CREATED_AT
 		, CREATED_BY
 		, UPDATED_AT
 		, UPDATED_BY)
		VALUES(
			  #{coCd}
			, #{dlCd}
			, #{dlCdNm}
			, #{dlCdEnm}
			, #{sortOdr}
			, #{txtVal1}
			, #{txtVal2}
			, #{txtVal3}
			, #{numVal4}
			, #{numVal5}
			, #{numVal6}
			, #{useYn}
			, #{remark}
			, #{createdAt}
			, #{createdBy}
			, #{updatedAt}
			, #{updatedBy}
		)

mapper.update:
UPDATE SM_DL_CD_INFO
SET
	  DL_CD_NM		=	#{dlCdNm}
	, DL_CD_ENM		=	#{dlCdEnm}
	, SORT_ODR		=	#{sortOdr}
	, TXT_VAL1		=	#{txtVal1}
	, TXT_VAL2		=	#{txtVal2}
	, TXT_VAL3		=	#{txtVal3}
	, NUM_VAL4		=	#{numVal4}
	, NUM_VAL5		=	#{numVal5}
	, NUM_VAL6		=	#{numVal6}
	, USE_YN		=	#{useYn}
	, REMARK		=	#{remark}
	, UPDATED_AT	=	#{updatedAt}
	, UPDATED_BY	=	#{updatedBy}
WHERE
	CO_CD = #{coCd}
	AND DL_CD = #{dlCd}

mapper.delete:
DELETE
	FROM SM_DL_CD_INFO
WHERE
	CO_CD = #{coCd}
	AND DL_CD = #{dlCd}
