	  VOC_NM		=	#{vocNm}
	, PLAY_ST_DATE	=	#{playStDate}
	, PLAY_TYPE		=	#{playType}
	, KR_TTS		=	#{krTts}
	, EN_TTS		=	#{enTts}
	, SCR_TXT		=	#{scrTxt}
