mapper.update:
UPDATE BM_VOC_INFO
SET
	  VOC_NM		=	#{vocNm}
	, SCR_TXT		= 	left(replace(#{scrTxt}, " ", ""), 4)
	, PLAY_TYPE		=	#{playType}
	, KR_TTS		=	#{krTts}
	, REMARK		=	#{remark}
	, PLAY_TM		=	#{playTm}
	, UPDATED_AT	=	#{updatedAt}
	, UPDATED_BY	=	#{updatedBy}
	, UPDATED_IP	=	#{updatedIp}
WHERE
	VOC_ID = #{vocId}
	AND
	VOC_DIV = 'CD029'
