	UPDATE BM_UPD_LOG
	SET
		  PROCE_RST = 'CD999'
		, SEND_DATE = NOW()
		, COMPLETE_YN = 'Y'
	WHERE
		RSV_ID = #{item.rsvId}
		AND MNG_ID = #{item.mngId}
;


